mapper.select:
select(SELECT count(1)
FROM oldman_base o   
WHERE  o.disable=0    

    and o.birthday > #{param3}


    and o.birthday < #{param2}


    and o.id=#{param1.entity.id}


    and o.xq_id in

        ${item}


 and o.sex= 2

    and o.id IN
    (SELECT oldman_id from home_oldman where home_id IN

        ${item.secType}
    )


    and o.id in
    (SELECT  oldman_id from organ_oldman where organ_id in

        ${item.id}
    )


    and (ohb.is_mb in

        ${item}

    or ohb.is_sn in

        ${item}

    or ohb.is_ywfy in

        ${item}

    or ohb.is_exzl in

        ${item}

    or ohb.is_gz in

        ${item}

    or ohb.is_cj in

        ${item}

    )


    and ohb.intelligence in

        ${item}



    and ohb.eyesight in

        ${item}




    and o.is_volunteer=#{param1.entity.isVolunteer}


    and

        (o.family like concat('%',#{item},'%'))



    and

        (o.family_type like concat('%',#{item},'%'))



    and

        (o.economic like concat('%',#{item},'%'))



    and o.census in

        ${item}



    and o.zc in

        ${item}




    and o.old_status in

        ${item}



    and o.political_status in

        ${item}



    and (o.pid LIKE  concat('%',#{param1.entity.search},'%') OR o.address LIKE  concat('%',#{param1.entity.search},'%') OR o.name LIKE  concat('%',#{param1.entity.search},'%')
    OR o.phone LIKE  concat('%',#{param1.entity.search},'%'))
)numOfSixToSevenM,
