mapper.update:
INSERT INTO view_backup
select ob.id oldman_id,ob.name name,ob.sex sex,ob.birthday birthday,ob.family family,ob.economic economic,
ob.is_handle is_handle,ob.key_status key_status,ob.census census,ob.xq_id xq_id,ob.old_status old_status,
oo.organ_id organ_id,
chx.home_id chx_id,zhucan.home_id zhucan_id,
zhujie.home_id zhujie_id,zhuji.home_id zhuji_id,zhuyu.home_id zhuyu_id,zhuxing.home_id zhuxing_id,zhuyi.home_id zhuyi_id,
kangfu.home_id kangfu_id,xiangtan.home_id xiangtan_id,xidi.home_id xidi_id,shenghuo.home_id shenghuo_id,
ankang.home_id ankang_id,kangle.home_id kangle_id,
t1.sec_type jtys_id,t2.sec_type jtbc_id,
ob.family_type,
ohb.intelligence,ohb.eyesight,
ohb.is_mb,ohb.is_sn,ohb.is_ywfy,ohb.is_exzl,ohb.is_gz,ohb.is_cj,
ob.political_status,ob.disable,ho.is_service,ob.is_volunteer,ob.pid
from oldman_base ob
left join home_oldman chx on ob.id=chx.oldman_id and chx.ny=#{param1} and (chx.home_id=1 or chx.home_id=2 or chx.home_id=3 or chx.home_id=4 or chx.home_id=5 or chx.home_id=6)
left join home_oldman zhucan on zhucan.oldman_id=ob.id and zhucan.home_id=7 and zhucan.ny=#{param1}
left join home_oldman zhujie on zhujie.oldman_id=ob.id and zhujie.home_id=8 and zhujie.ny=#{param1}
left join home_oldman zhuji on zhuji.oldman_id=ob.id and zhuji.home_id=9 and zhuji.ny=#{param1}
left join home_oldman zhuyu on zhuyu.oldman_id=ob.id and zhuyu.home_id=10 and zhuyu.ny=#{param1}
left join home_oldman zhuxing on zhuxing.oldman_id=ob.id and zhuxing.home_id=11 and zhuxing.ny=#{param1}
left join home_oldman zhuyi on zhuyi.oldman_id=ob.id and zhuyi.home_id=12 and zhuyi.ny=#{param1}
left join home_oldman kangfu on kangfu.oldman_id=ob.id and kangfu.home_id=13 and kangfu.ny=#{param1}
left join home_oldman xiangtan on xiangtan.oldman_id=ob.id and xiangtan.home_id=14 and xiangtan.ny=#{param1}
left join home_oldman xidi on xidi.oldman_id=ob.id and xidi.home_id=15 and xidi.ny=#{param1}
left join home_oldman shenghuo on shenghuo.oldman_id=ob.id and shenghuo.home_id=16 and shenghuo.ny=#{param1}
left join home_oldman ankang on ankang.oldman_id=ob.id and ankang.home_id=17 and ankang.ny=#{param1}
left join home_oldman kangle on kangle.oldman_id=ob.id and kangle.home_id=18 and kangle.ny=#{param1}
left join (select jtys.oldman_id,home_jtys.sec_type from home_oldman jtys,home home_jtys where jtys.home_id=home_jtys.id and home_jtys.fir_type=4 and jtys.ny=#{param1})t1
on t1.oldman_id=ob.id
left join (select jtbc.oldman_id,home_jtbc.sec_type  from home_oldman jtbc,home home_jtbc where jtbc.home_id=home_jtbc.id and home_jtbc.fir_type=5 and jtbc.ny=#{param1})t2
on t2.oldman_id=ob.id
left join organ_oldman oo on oo.oldman_id=ob.id and oo.ny=#{param1}
left join oldman_health_base ohb on ohb.oldman_id=ob.id
left join home_oldman ho on ho.oldman_id=ob.id and ho.ny=#{param1}
where ob.disable!=1
